#This script query's AD and gets available names based on the VMBaseName.  It reserves the names for 2 hours to allow for VM creation and AD replication

#Set credentials to use in PowerShell commands
$uname = "@@{domaindeploycred.username}@@"
$passwd = ConvertTo-SecureString -AsPlainText '@@{domaindeploycred.secret}@@' -Force
$cred = New-Object System.Management.Automation.PSCredential ($uname,$passwd)

#Set the first part of the computer name from CALM variables set at the blueprint level
$VMBaseName = "jb@@{sitecode}@@m@@{appcode}@@@@{environment}@@"

#Check that the basename is not greater than 12 characters long
If ($VMBaseName.Length -gt "12"){

    Write-Output "Basename, $VMBaseName, is too long. The max length is 12 characters"
    exit 1

}

#Set the number of VMs to be created
[int]$VMCount = @@{vmCount}@@

#Check that the VM count does not exceed 99
If ($VMCount -gt "99"){

    Write-Output "VM count, $VMCount, exceeds the size limit of 99.  Create fewer VMs"
    exit 1

}

#Set the log path
$ReservedNamesLogPath = "C:\Scripts\Get-Available-Names\ReservedNames"

#If the log folder is missing, create it
If (-Not (Test-Path $ReservedNamesLogPath)) {

    New-Item -ItemType Directory -Path $ReservedNamesLogPath

}

#Check if basename ends in 99, which indicates the VM is a Sandbox VM. NameUniverse should stop at 99
If ($VMBaseName -match '9{2}$'){

    Write-Output "Basename ends in 99. VM is in the Sandbox environment."

    $NameUniverse = @()
    For($i=0; $i -le 99; $i++){

        $i_string = $i.ToString("00")
        $Name = "${VMBaseName}${i_string}"
        $NameUniverse += $Name

    }

#Check if basename ends in 9, which indicates the VM is a Dev VM. NameUniverse should stop at 899
} ElseIf ($VMBaseName -match '9{1}$'){

    Write-Output "Basename ends in 9. VM is in the Dev environment."

    $NameUniverse = @()
    For($i=0; $i -le 899; $i++){

        $i_string = $i.ToString("000")
        $Name = "${VMBaseName}${i_string}"
        $NameUniverse += $Name

    }

#If the basename does not end in a 9 or 99, increment the NameUniverse to 999
} Else {

    Write-Output "Basename is neither 9 or 99."

    $NameUniverse = @()
    for($i=0; $i -le 999; $i++){
        $i_string = $i.ToString("000")
        $Name = "${VMBaseName}${i_string}"
        $NameUniverse += $Name

        }
}

#Get all computer accounts from AD and filter down to those that currently match the base name
Try {

    $DetectedNames = (Get-ADComputer -Filter * -Credential $cred -WarningAction SilentlyContinue -ErrorAction Stop).Name | Select-String $VMBaseName | Foreach {"$_"}

} Catch {

    Write-Output "An error occured querying AD. Terminating script"
    exit 1

}

#Release reserved names that are older than 2 hours
Get-ChildItem -Path $ReservedNamesLogPath | Where-Object {$_.LastWriteTime -lt (date).AddMinutes(-120)} | Remove-Item

#Get remaining reserved names
$ReservedNames = Get-ChildItem -Path $ReservedNamesLogPath

#If remaining reserved names are found, trim the file extention from the names.
If ($ReservedNames){

    $ReservedNames = $ReservedNames.name.trim(".txt")

}

#Get available names based on what is not currently used in AD or reserved
$AvailableNamesList = $NameUniverse | Where-Object {$DetectedNames -notcontains $_} | Where-Object {$ReservedNames -notcontains $_}

#Reserve names before build to minimize contention
$AvailableNamesToReserve = $AvailableNamesList | Select -First $VMCount

#Write the reserved names to a log so they won't be reused for 2 hours
ForEach ($ReservedName in $AvailableNamesToReserve){

    New-Item -Path $ReservedNamesLogPath -Name "${ReservedName}.txt" -ItemType File

}

#Add a comma to the end of each name to prep it for Calm
$AvailableNames = @()
ForEach ($ReservedName in $AvailableNamesToReserve){

    $AvailableNames += $ReservedName+","

}

#Remove the spaces from the string array
$AvailableNames = "$AvailableNames" -replace "`n","" -replace " ",""
Write-Output "The reserved names for this deployment are $AvailableNames"
Write-Output ""

#Output the results for Calm
Write-Output "availableName=$AvailableNames"
Write-Output ""

exit 0

$CompName = $Env:ComputerName

#Add accounts to the Administrators local group
Write-Output "Adding groups to the local Administrators group."

#Determine if the server is a SQL server
If($CompName -like "*MSQL*"){

    #If SQL server, these are the groups to be added to the administrators local group
    Write-Output "$CompName is a SQL server."
    $GMembers = "contoso\dba"

} Else {

    #If not SQL server, these are the groups to be added to the administrators local group
    Write-Output "$CompName is not a SQL server."
    $GMembers = "contoso\it_support"

}

#Add members to the local Administrators group
ForEach ($GMember in $GMembers){

    $ExistingMember = Get-LocalGroupMember -Name "Administrators" | Where {$_.Name -eq "$GMember"}

    If ($ExistingMember) {

        Write-Output "$GMember is already a member of the local Administrator group"

        } Else {

            Try {

                Add-LocalGroupMember -Name "Administrators" -Member $GMember -ErrorAction Stop -ErrorVariable AddGroupErrors
                Write-Output "$GMember added to the local administrators group"

            } Catch {

                Write-Output "$AddGroupErrors"
                exit 1

            }  
    }
}

#Disable local Administrators account

$LocalUserName = "Administrator"

$UserInfo = Get-LocalUser -Name $LocalUserName
$UserStatus = $UserInfo.Enabled

If($UserStatus -eq $true){

    Disable-LocalUser $LocalUserName
    Write-Output "Disabled local account, $LocalUserName"

} Else {

    Write-Output "$LocalUserName is already disabled"

}

