#script

prism_ip = '@@{PC_IP}@@'
username = '@@{PC_USER}@@'
password = '@@{PC_PASSWORD}@@'
application_names = '@@{APPLICATION_NAMES}@@'
action_name = "Upgrade"
total_matches = 0
count = 0
parallel_process = 3
apps_list_uuid = []
api_url = "https://" + prism_ip + ":9440/api/nutanix/v3"

print "INFO: Retrieving application list of given application names: {}.".format(application_names)

# -*- Get Application list UUID
for app_name in application_names.split(','):

    get_apps_uuid_url = api_url + "/apps/list" 
    filter_query = "_state==running;name==" + app_name
    payload = {
            "filter": filter_query
        }
    headers = {"Content-Type": "application/json"}
    try:
        response = urlreq(get_apps_uuid_url, verb='POST', auth="BASIC", user=username, passwd=password, params=json.dumps(payload), headers=headers, verify=False)
        apps_content = json.loads(response.content)
        apps_list_uuid.append(apps_content['entities'][0]['metadata']['uuid'])
    except Exception as e:
        print "ERROR: Failed to get application '{}' it doesn't exists or check username/password".format(app_name)
        print e.message

total_matches = len(apps_list_uuid)
if  total_matches == 0:
    print "WARNING: No applications found with given names: {}.".format(application_names)
    exit(1)

while (count < total_matches):
    runlog_ids = {}
    first_item = count
    last_item = first_item + parallel_process
    if total_matches < last_item:
        last_item = total_matches
    number_of_executions = last_item-first_item
    success_fail_apps = 0
    for app_uuid in apps_list_uuid[first_item:last_item]:
        launch_apps_action_url = api_url + "/apps/{}/actions/run".format(app_uuid)

        payload = {
                "name": action_name
            }
        headers = {"Content-Type": "application/json"}
        try:
            response = urlreq(launch_apps_action_url, verb='POST', auth="BASIC", user=username, passwd=password, params=json.dumps(payload), headers=headers, verify=False)
            action_run_content = json.loads(response.content)
            action_runlog_id = action_run_content['runlog_uuid']
        except Exception as e:
            print "ERROR: Failed to run application action: {}".format(action_name)
            print e.message
            exit(1)
        status_uuid = {"state" : "PENDING", "app_uuid" : app_uuid}
        print "INFO: Running Action '{}' for application id: '{}' runlog id: '{}'".format(action_name, app_uuid, action_runlog_id)
        runlog_ids[action_runlog_id] = status_uuid
    count += parallel_process

    while True:
        for runlog_id in runlog_ids.keys():
            print "INFO: Checking run log: {}".format(runlog_id)
            if runlog_ids[runlog_id]["state"] == 'PENDING' or runlog_ids[runlog_id]["state"] == 'RUNNING':
                poll_app_uuid = runlog_ids[runlog_id]['app_uuid']
                runlog_status_url = api_url + "/apps/{}/app_runlogs/{}".format(poll_app_uuid,runlog_id)
                response = urlreq(runlog_status_url, verb='GET', auth="BASIC", user=username, passwd=password, verify=False)
                poll_runlog_content = json.loads(response.content)
                if poll_runlog_content['status']['state'] == 'SUCCESS':
                    runlog_ids[runlog_id]["state"] = 'SUCCESS'
                    print "INFO: Action {} is sucessful for application id: {}.".format(action_name,poll_app_uuid)
                    success_fail_apps += 1
                elif poll_runlog_content['status']['state'] == 'RUNNING' or poll_runlog_content['status']['state'] == 'PENDING':
                    print "INFO: Action {} is in progress for application id: {}.".format(action_name,poll_app_uuid)
                else:
                    runlog_ids[runlog_id]["state"] = 'FAILED'
                    print "ERROR: Action {} for application id: {} failed.".format(action_name,poll_app_uuid)
                    success_fail_apps += 1
        if success_fail_apps == number_of_executions:
            break
        sleep(5)

