attrs.script:
$ErrorActionPreference = "Stop"

$DomainName = "@@{DomainName}@@"
$CalmArrayIndex = @@{calm_array_index}@@
$ServerIPs = ("@@{ActiveDirectory.address}@@").Split(",")
$SafeModeAdministratorCredential = '@@{Windows.secret}@@' | ConvertTo-SecureString -AsPlainText -Force
$ADDomainCredential = New-Object pscredential -ArgumentList "@@{Windows.username}@@@$DomainName",$SafeModeAdministratorCredential
$DomainContext = New-Object System.DirectoryServices.ActiveDirectory.DirectoryContext('Domain', $DomainName, $ADDomainCredential.UserName, $ADDomainCredential.GetNetworkCredential().Password)

if ($CalmArrayIndex -eq 0)
{
  # Install Forest

  $ForestParams = @{
      DomainName = "$DomainName"
      DomainMode = "WinThreshold"
      Force = $true
      ForestMode = "WinThreshold"
      SafeModeAdministratorPassword = $SafeModeAdministratorCredential
      NoRebootOnCompletion = $true
      WarningAction = "SilentlyContinue"
  }

  Write-Output "Installing AD Forest $DomainName."

  $Forest = Install-ADDSForest @ForestParams

  # Set DNS
  Set-DnsClientServerAddress -InterfaceAlias Ethernet -ResetServerAddresses
  Set-DnsClient -ConnectionSpecificSuffix $DomainName -InterfaceAlias Ethernet
  Set-DnsClientServerAddress -InterfaceAlias Ethernet -Addresses @@{address}@@,$ServerIPs[1]
  #reg add HKLM\SYSTEM\CurrentControlSet\Services\NTDS\Parameters /v "Repl Perform Initial Synchronization" /t REG_DWORD /d 0

  if (-not $Forest.Status -eq "Success") { exit 2 } else { Write-Output "AD Forest installed Successfully."}

}
elseif ($CalmArrayIndex -gt 0)
{
    # Install Domain Controller

    Set-DnsClient -ConnectionSpecificSuffix $DomainName -InterfaceAlias Ethernet
    Set-DnsClientServerAddress -InterfaceAlias Ethernet -Addresses $ServerIPs[0],@@{address}@@

    # Wait for the first DC to come online
    Write-Output "Waiting for $DomainName to become accessible."
    for ($i = 0; $i -le 90 ; $i++)
    {
        try
        {
            if (([System.DirectoryServices.ActiveDirectory.DomainController]::FindOne($DomainContext)).domain.ToString() -eq $DomainName) { break }
        }
        catch
        {
			# Cleanup errors we catch
        	$error.clear()
        }

        Start-Sleep -Seconds 15
    }

    $DCParams = @{
        Credential = $ADDomainCredential
        DomainName = "$DomainName"
        Force = $true
        SafeModeAdministratorPassword = $SafeModeAdministratorCredential
        NoRebootOnCompletion = $true
        WarningAction = "SilentlyContinue"
    }

    Write-Output "Adding new Domain Controller to $DomainName."

    $DomainController = Install-ADDSDomainController @DCParams

    if (-not $DomainController.Status -eq "Success") { exit 2 } else { Write-Output "Domain Controller added to $DomainName successfully."}

}

Set-CertificateAutoEnrollmentPolicy -EnableAll -Context Machine

Restart-Computer -Force

